import { initializeApp } from "firebase/app";
import { getAuth, signInWithEmailAndPassword } from "firebase/auth"
import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";



function Login() {

    const [email, setEmail] = useState("")
    const [pswd, setPswd] = useState("")

    const navigate = useNavigate()




    const firebaseConfig = {
        apiKey: "AIzaSyDZDYClrk3wX0zQARqtQy3r7DaFYuSjleo",
        authDomain: "karthik-30af1.firebaseapp.com",
        projectId: "karthik-30af1",
        storageBucket: "karthik-30af1.appspot.com",
        messagingSenderId: "390068929098",
        appId: "1:390068929098:web:b515e36120e6404d905cb3",
        measurementId: "G-X1CEJRWMMH"
    };

    initializeApp(firebaseConfig);
    const auth = getAuth()

    const submitData = (event) => {
        event.preventDefault()

        let obj = {
            email: email,
            password: pswd
        }
        signInWithEmailAndPassword(auth, obj.email, obj.password)

            .then(() => {
                alert("logged in Successfully")
                navigate("/main")
            })
            .catch((err) => {
                alert("Error", err)
            })
    }



    return (
        <div>
        <div style={{margin:0,display:"flex",justifyContent:"center",alignItems:"center"}}>
            {/* <h3 className="text-center">Login</h3> */}
         
            <form onSubmit={submitData}>
                <label>Email</label>
                <input value={email} onChange={e => setEmail(e.target.value)} type="email" class="form-control" required id="exampleInputEmail1" aria-describedby="emailHelp" />

                <label>Password</label>
                <input value={pswd} onChange={e => setPswd(e.target.value)} type="password" class="form-control" required id="exampleInputPassword1" /> <br />

                <button type="submit" className="btn btn-primary">Login</button>

                <a>If you dont have a account
                <Link to="/">Register</Link>
            </a>
            </form>

           
        </div>
        </div>
    )
}
export default Login;
