import { useState,useEffect } from "react"
import { Link } from "react-router-dom"


function Cart(){
   
    const [data, setData] = useState([])

  const [img,setImg]=useState("")
  const [name,setName]=useState("")
  const [content,setContent]=useState("")
  const [price,setPrice]=useState("")




    useEffect(() => {
        fetch("http://localhost:3006/cart-data")
          .then((res) => {
            return res.json()
          })
          .then((resp) => {
            setImg(resp.img)
          })
      }, []) 



    return(
        <div>
              <div className="row my-2">
        {data.map((item)=>(

          <div className= "shadow col-sm-3 mb-3" >
            <div className="card h-90">

            <div className="card-body">
                <img src={item.img} value={img} onChange={e=>setImg(e.target.value)} className="mb-2" alt="kk" height="200px" width="100%" />
                <h5 className="card-title" value={name} onChange={e=>setName(e.target.value)} >{item.name}</h5>
                <p className="card-text" value={content} onChange={e=>setContent(e.target.value)} >{item.content}</p>
                <h4 value={price} onChange={e=>setPrice(e.target.value)} >Rs.{item.price}$</h4>
                <a href="#" className="btn btn-primary">
                    <Link to={"/main"}>REMOVE</Link>
                </a>
              </div>

            </div>
          </div>

        ))}
      </div>
        </div>
    )
}
export default Cart;
