import { useEffect, useState } from "react"
import { signOut, getAuth } from "firebase/auth";
import { initializeApp } from "firebase/app";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
// import "./commerce.css"


function Free() {
  let plus = 0
  const [data, setData] = useState([])
  const [value, setValue] = useState("")
  const [img, setImg] = useState("")
  const [name, setName] = useState("")
  const [content, setContent] = useState("")
  const [price, setPrice] = useState("")

  const senddata = (id) => {
    fetch("http://localhost:3006/e-commerce/" + id)
      .then((res) => {
        return res.json()
      })
      .then((resp) => {
        console.log(resp)
        fetch("http://localhost:3006/cart-data", {
          method: "POST",
          headers: { "Content-type": "application/json" },
          body: JSON.stringify(resp)
        })
          .then(() => {
            alert("kkkkkkkk")
          })
      }).catch((err) => {
        alert(err)
      })
  }

  useEffect(() => {
    fetch(" http://localhost:3006/e-commerce")
      .then((res) => {
        return res.json()
      })
      .then((resp) => {
        setData(resp)
      })
  }, [])

  const search = async (kk) => {
    kk.preventDefault()
    return await axios.get(`http://localhost:3006/e-commerce?q=${value}`)
      .then((g) => {
        setData(g.data)
      })
      .catch((err) => {
        console.log(err)

      })
  }

  const change = (s) => {
    setValue(s.target.value)
  }

  const navigate = useNavigate()
  const firebaseConfig = {
    apiKey: "AIzaSyDZDYClrk3wX0zQARqtQy3r7DaFYuSjleo",
    authDomain: "karthik-30af1.firebaseapp.com",
    projectId: "karthik-30af1",
    storageBucket: "karthik-30af1.appspot.com",
    messagingSenderId: "390068929098",
    appId: "1:390068929098:web:b515e36120e6404d905cb3",
    measurementId: "G-X1CEJRWMMH"
  };

  initializeApp(firebaseConfig);
  const auth = getAuth()

  const submitData = (event) => {
    event.preventDefault()


    signOut(auth)

      .then(() => {
        alert("logged out Successfully")
        navigate("/login")

      })
      .catch((err) => {
        alert("Error", err)
      })

  }

  return (
    <div>
      <nav className="navbar bg-dark border-bottom border-body" data-bs-theme="dark">
        <div className="container-fluid">
          <a className="navbar-brand"><b style={{ color: "rgb(161, 255, 66)" }}>DOG</b><i className="fa-solid fa-dog fa-bounce fa-xl" style={{ color: "#df9834" }} /><sub style={{ color: "rgb(222, 18, 175)" }}>KART</sub></a>
