import { useState, useEffect } from "react"
import { Link, } from "react-router-dom"


function Cart() {

  const [data, setData] = useState([])
  const [total,setTotal]=useState(resp)

  useEffect(() => {
    fetch("http://localhost:3006/cart-data")
      .then((res) => {
        return res.json()
      })
      .then((resp) => {
        setData(resp)

        // const total=resp.data.reduce((acc,item)=>acc+parseFloat(item.price),0)
        // setTotal(total)

      })
  }, [])

const totalprice=()=>{
  return total.reduce((total,item)=>total+item.price)
}

  const deletcart = (id) => {
    fetch("http://localhost:3006/cart-data/" + id, {
      method: "DELETE"
    })
      .then(() => {
        // alert("deleted")
        window.location.reload()
      })
      .catch(() => {
        alert("error")
      })
  }

  return (
    <div>

      <nav className="navbar bg-dark border-bottom border-body" data-bs-theme="dark">
          
      </nav>


      <table className="table">
        <thead>
          <tr>
            <th scope="col">S.no</th>
            <th scope="col">img</th>
            <th scope="col">name</th>
            <th scope="col">content</th>
            <th scope="col">price</th>
          </tr>
        </thead>
        <tbody class="table-group-divider">

          {data.map((item) => (
            <tr>
              <th scope="row">{item.id}</th>
              <td><img src={item.img} alt="k" height="60px" width="50%"/></td>
              <td>{item.name}</td>
              <td>{item.content}</td>
              <td>{item.price}</td>
              <button className="btn btn-danger" onClick={()=>{deletcart(item.id)}}>Remove</button>
            </tr>
          ))}
        </tbody>
      </table>

      <button className="btn btn-outline-primary">
      <Link to={"/main"}>BACK</Link></button><br/><br/>

      <h3 style={{marginLeft:"80%"}}>Total:Rs{totalprice()}</h3>
    </div>
    
  )
}
export default Cart;
