function Edit(){

    const [id, setID] = useState("")
    const [name, setName] = useState("")
    const [email, setEmail] = useState("")
    const [mobail, setMobail] = useState("")
    const [city, setCity] = useState("")


    const Setdata = (e) => {
        e.preventDefault()
        let data={id,name,email,mobail,city}
        fetch("http://localhost:3006/employes_details",{
            method: "POST",
            headers: { "Content-type": "application/json" },
            body: JSON.stringify(data)
        })
            .then(() => {
                alert("data saved succesfully")
            })
            .catch(() => {
                alert("error")
            })
    }
    return(
        <div>
            <div className="container" style={{ width: "600px" }}>
                <div className="card">
                    <div className="card-title">
                        <h3>Add new employ details</h3>
                    </div>
                    <div className="card-body">

                        <form>
                            <div class="mb-3">
                                <label className="form-label">Id</label>
                                <input type="number" disabled="disabled" value={id} onChange={e => setID(e.target.value)} className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                            </div>

                            <div class="mb-3">
                                <label className="form-label">Name</label>
                                <input type="text" value={name} onChange={e => setName(e.target.value)} className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                            </div>

                            <div class="mb-3">
                                <label className="form-label">Email address</label>
                                <input type="email" value={email} onChange={e => setEmail(e.target.value)} className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                            </div>

                            <div class="mb-3">
                                <label className="form-label">Mobail no</label>
                                <input type="number" value={mobail} onChange={e => setMobail(e.target.value)} className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                            </div>

                            <div class="mb-3">
                                <label className="form-label">City</label>
                                <input type="text" value={city} onChange={e => setCity(e.target.value)} className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                            </div>

                            <button type="submit" className="btn btn-primary">Add new</button>
                            {/* <Link to="/" className="btn btn-danger">Back</Link> */}
                        </form>


                    </div>

                </div>
            </div>
        </div>
    )
}

export default Edit;
