import { useEffect, useState } from "react"
import "./commerce.css"

function Free() {
  const [data, setData] = useState([])
  useEffect(() => {
    fetch("http://localhost:3006/e-commerce")
      .then((res) => {
        return res.json()
      })
      .then((resp) => {
        setData(resp)
      })
  }, [])
  return (
    <div id="main">
      {data.map((item) => (

        <div className="card"  style={{ width: "18rem" }}>

          <img src={item.img} className="card-img-top" alt="kk" style={{height:"100px",width:"200px"}} />

          <div className="card-body">
            <h5 className="card-title">{item.name}</h5>
            <p className="card-text">{item.content}</p>
              <span style={{backgroundColor:"green", padding:"4px", borderRadius:"5px"}}>{item.price}$</span>  <a href="#" className="btn btn-primary">ADD(+)</a>
          </div>

        </div>

      ))}
    </div>
  )
}
export default Free;




