          <div className= "shadow col-sm-3 mb-3" >
            <div className="card h-90">

              <div className="card-body">
                <img src={item.img} value={img} onChange={e=>setImg(e.target.value)} className="mb-2" alt="kk" height="200px" width="100%" />
