// Authentication 
// Sign Up  - Registartion
// Login
// Logout
import { initializeApp } from "firebase/app";
import {  getAuth, signInWithEmailAndPassword } from "firebase/auth"
import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";



// useNavigate() -- routers
//     used to redirect the page

function Login(){

    const imagee={
        backgroundImage:`url("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRGL32wW8PDoivpX2SYijmFOuAzaK6jsGWTKA&usqp=CAU")`,
        backgroundSize:"cover",
        width:"100%",
        height:"400px"

    }
   
    const [email,setEmail]=useState("")
    const [pswd,setPswd]=useState("")
   
    const navigate=useNavigate() 


    

    const firebaseConfig = {
        apiKey: "AIzaSyDZDYClrk3wX0zQARqtQy3r7DaFYuSjleo",
        authDomain: "karthik-30af1.firebaseapp.com",
        projectId: "karthik-30af1",
        storageBucket: "karthik-30af1.appspot.com",
        messagingSenderId: "390068929098",
        appId: "1:390068929098:web:b515e36120e6404d905cb3",
        measurementId: "G-X1CEJRWMMH"
      };

     initializeApp(firebaseConfig);
    const auth = getAuth() // email / password auth
    
    const submitData=(event)=>{
        event.preventDefault()

        let obj={
            email:email,
            password:pswd
        }
        signInWithEmailAndPassword(auth,obj.email,obj.password)  //authentication , email ,password

        .then(()=>{
            alert("logged in Successfully")
            navigate("/main")
           
        })
        .catch((err)=>{
            alert("Error", err)
        })
    }



    return (
        <div>
        <div className="container" style={{width: "400px",marginTop:"100px"}}>
            <div className="card p-3">
                <div className="card-title">
                    <h3 className="text-center">Login</h3>
                </div>
                <div className="card-body" style={imagee}>
                    <form onSubmit={submitData}>
                      
                        <div className="mb-3">
                            <label className="form-label">Email</label>
                            <input value={email} onChange={e=>setEmail(e.target.value)} type="email" class="form-control" required id="exampleInputEmail1" aria-describedby="emailHelp" />
                        </div>
                        <div className="mb-3">
                            <label className="form-label">Password</label>
                            <input value={pswd} onChange={e=>setPswd(e.target.value)} type="password" class="form-control" required id="exampleInputPassword1" />
                        </div>
                     

                        <button type="submit" className="btn btn-primary">Login</button>
                    </form>
                     <div>
                        <a>If you dont have a account
                            <Link to="/">Register</Link>
                        </a>
                     </div>

                </div>
            </div>

        </div>
        </div>
    )
}

export default Login;
