import { useState, useEffect } from "react"
import { Link, } from "react-router-dom"


function Cart() {

  const [data, setData] = useState([])

  useEffect(() => {
    fetch("http://localhost:3006/cart-data")
      .then((res) => {
        return res.json()
      })
      .then((resp) => {
        setData(resp)
      })
  }, [])

  const deletcart = (id) => {
    fetch("http://localhost:3006/cart-data/" + id, {
      method: "DELETE"
    })
      .then(() => {
        alert("deleted")
        window.location.reload()
      })
      .catch(() => {
        alert("error")
      })
  }

  return (
    <div>

      <nav className="navbar bg-dark border-bottom border-body" data-bs-theme="dark">

        <a className="navbar-brand">
          <Link to={"/main"}>BACK</Link>
        </a>
      </nav>

      {data.map((item) => (
          <table class="table">
  <thead>
    <tr>
      <th scope="col">S.no</th>
      <th scope="col">img</th>
      <th scope="col">name</th>
      <th scope="col">content</th>
      <th scope="col">price</th>
    </tr>
  </thead>
  <tbody class="table-group-divider">
    <tr>
      <th scope="row">1</th>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <th scope="row">2</th>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <th scope="row">3</th>
      <td>Larry the Bird</td>
      <td>Larry the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</table>
      ))}
      <button className="btn btn-outline-primary">BACK</button>

    </div>
  )
}
export default Cart;
