import { useState, useEffect } from "react"
import { Link, } from "react-router-dom"


function Cart() {

  const [data, setData] = useState([])

  useEffect(() => {
    fetch("http://localhost:3006/cart-data")
      .then((res) => {
        return res.json()
      })
      .then((resp) => {
        setData(resp)
      })
  }, [])

  const deletcart = (id) => {
    fetch("http://localhost:3006/cart-data/" + id, {
      method: "DELETE"
    })
      .then(() => {
        alert("deleted")
        window.location.reload()
      })
      .catch(() => {
        alert("error")
      })
  }

  return (
    <div>

      <nav className="navbar bg-dark border-bottom border-body" data-bs-theme="dark">

      <a className="navbar-brand">
          <Link to={"/cart"}>BACK</Link>
          </a>
      </nav>

      <div className="row my-2">
        {data.map((item) => (

          <div className="shadow col-sm-3 mb-3" >
            <div className="card h-90">

              <div className="card-body">
                <img src={item.img} className="mb-2" alt="kk" height="200px" width="100%" />
                <h5 className="card-title">{item.name}</h5>
                <p className="card-text">{item.content}</p>
                <h4>Rs.{item.price}$</h4>
                <a href="#" className="btn btn-primary" onClick={() => { deletcart(item.id) }}>REMOVE</a>
              </div>

            </div>
          </div>

        ))}
      </div>
    </div>
  )
}
export default Cart;
