// Authentication 
// Sign Up  - Registartion
// Login
// Logout
import { initializeApp } from "firebase/app";
import { createUserWithEmailAndPassword, getAuth } from "firebase/auth"
import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

function App() {
    const [name,setName]=useState("")
    const [email,setEmail]=useState("")
    const [pswd,setPswd]=useState("")
    const [cpswd,setCpswd]=useState("")
    const navigate=useNavigate()


    

    const firebaseConfig = {
        apiKey: "AIzaSyDZDYClrk3wX0zQARqtQy3r7DaFYuSjleo",
        authDomain: "karthik-30af1.firebaseapp.com",
        projectId: "karthik-30af1",
        storageBucket: "karthik-30af1.appspot.com",
        messagingSenderId: "390068929098",
        appId: "1:390068929098:web:b515e36120e6404d905cb3",
        measurementId: "G-X1CEJRWMMH"
      };

     initializeApp(firebaseConfig);
    const auth = getAuth() // email / password auth
    
    const submitData=(event)=>{
        event.preventDefault()
        if(name.length <=5){
            alert("Minimu 6 charachter manadatoery..!")
        }
        if(pswd != cpswd){
            alert("Password does not match..!")
        }

        let obj={
            email:email,
            password:pswd
        }
        createUserWithEmailAndPassword(auth,obj.email,obj.password)  //authentication , email ,password

        .then(()=>{
            alert("Registered Successfully")
            navigate("/login")
        })
        .catch((err)=>{
            alert("Error", err)
        })


    }

    return (
        <div className="container" style={{ width: "400px" }}>
            <div className="card p-3">
                <div className="card-title">
                    <h3 className="text-center">SignUp</h3>
                </div>
                <div className="card-body">
                    <form onSubmit={submitData}>
                        <div class="mb-3">
                            <label class="form-label">Name</label>
                            <input value={name} type="text" onChange={e=>setName(e.target.value)} class="form-control" required id="exampleInputEmail1" aria-describedby="emailHelp" />
                        </div>
                        <div class="mb-3">
                            <label class="form-label">Email</label>
                            <input value={email} onChange={e=>setEmail(e.target.value)} type="email" class="form-control" required id="exampleInputEmail1" aria-describedby="emailHelp" />
                        </div>
                        <div class="mb-3">
                            <label class="form-label">Password</label>
                            <input value={pswd} onChange={e=>setPswd(e.target.value)} type="password" class="form-control" required id="exampleInputPassword1" />
                        </div>
                        <div class="mb-3">
                            <label class="form-label">Confirm Password</label>
                            <input value={cpswd} onChange={e=>setCpswd(e.target.value)}  type="password" class="form-control" required id="exampleInputPassword1" />
                        </div>

                        <button type="submit" class="btn btn-primary">Register</button>
                    </form>

                    <div>
                        <a href="">If you have already account
                            <Link to="/login">Login</Link>
                        </a>
                     </div>
                </div>
            </div>

        </div>
    )
}

export default App;
