import { useEffect, useState } from "react"
import { signOut,getAuth } from "firebase/auth";
import { initializeApp } from "firebase/app";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./commerce.css"

function Free() {
  const [data, setData] = useState([])
  const [value,setValue]=useState("")
  useEffect(() => {
    fetch(" http://localhost:3006/e-commerce")
      .then((res) => {
        return res.json()
      })
      .then((resp) => {
        setData(resp)
      })
  }, [])

  const search=async(kk)=>{
    kk.preventDefault()
    return await axios.get(`http://localhost:3006/e-commerce?q=${value}`)
    .then((g)=>{
      setData(g.data)
    })
    .catch((err)=>{

    })
  }

  onchange=(s)=>{
    setValue(s.target.value)
  }

  const navigate=useNavigate()
  const firebaseConfig = {
      apiKey: "AIzaSyDZDYClrk3wX0zQARqtQy3r7DaFYuSjleo",
      authDomain: "karthik-30af1.firebaseapp.com",
      projectId: "karthik-30af1",
      storageBucket: "karthik-30af1.appspot.com",
      messagingSenderId: "390068929098",
      appId: "1:390068929098:web:b515e36120e6404d905cb3",
      measurementId: "G-X1CEJRWMMH"
    };

   initializeApp(firebaseConfig);
  const auth = getAuth()
  
  const submitData=(event)=>{
      event.preventDefault()

     
      signOut(auth)

      .then(()=>{
          alert("logged out Successfully")
          navigate("/login")
         
      })
      .catch((err)=>{
          alert("Error", err)
      })

    }
  return (
    <div>
      <nav className="navbar bg-dark border-bottom border-body" data-bs-theme="dark">
        <div className="container-fluid">
          <a className="navbar-brand">LOGO</a>
          <a className="navbar-brand">Home</a>
          <a className="navbar-brand">Cart</a>

          <form className="d-flex" role="search" onSubmit={search}>
            <input className="form-control me-2" type="search" placeholder="Search" value={value} aria-label="Search" />
            <button className="btn btn-outline-success" type="submit">Search</button>
          </form>
          <button className="btn btn-outline-success" onClick={submitData} type="text">Log-Out</button>
        </div>
      </nav>

      <div className="row my-2">
        {data.map((item)=>(

          <div className= "shadow col-sm-3 mb-3" >
            <div className="card h-90">

              <div className="card-body">
                <img src={item.img} className="mb-2" alt="kk" height="200px" width="100%" />
                <h5 className="card-title">{item.name}</h5>
                <p className="card-text">{item.content}</p>
                <h4>Rs.{item.price}$</h4>
                <a href="#" className="btn btn-primary">ADD CART(+)</a>
              </div>

            </div>
          </div>

        ))}
      </div>
    </div>

  )
}
export default Free;
